INSERT INTO public.notas VALUES (4685, 1, 888, 1251, '<p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">Não há necessidade que todo Comprador tenha um atributo Observador leilao_maior_lance pois quem deve saber o maior lance é o leiloeiro.</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">É responsabilidade do leiloeiro notificar aos participantes do leilão qual é o maior lance.</p>', NULL);
