INSERT INTO public.notas VALUES (5114, 3.6, 899, 1280, '<p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">FELIPE PORTO (3.6/4)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">1) (2.0) Cadastre Clientes e seus respectivos Endereços utilizando Java/JPA e PostgreSQL.&nbsp;</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">- O menu fica em loop infinito (-0.2)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">2) (2.0) Armazene e Recupere as configurações pessoais dos Clientes com REDIS.</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">- O menu fica em loop infinito (-0.2)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">- obs: poderia ter armazenado somente as configurações</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">obs gerais:&nbsp;</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">* poderia ter feito tudo em um mesmo projeto</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">* classes java devem começar com letra maiúscula</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">===============</p>', NULL);
INSERT INTO public.notas VALUES (5116, 4, 899, 1282, '<p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">GABRIEL VEIGA&nbsp; (3.5 + 0.5 = 4)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">Atividade Avaliada&nbsp; (3.5/4)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">1) (2.0) Cadastre Clientes e seus respectivos Endereços utilizando Java/JPA e PostgreSQL.&nbsp;</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">- problemas de mapeamento (-0.5)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">* na tabela gerada de cliente há mais uma outra coluna correspondente ao endereço além de sua pk correspondente (que aqui funciona como fk da tabela endereço na tabela de endereço cliente)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">- o mapeamento jpa deveria ser feito diretamente nas classes de modelo. É improdutivo criar classes de modelo e classes Entity. Imagine o retrabalho caso haja a adição de um</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">novo atributo na classe Cliente (por exemplo): seria preciso criar um novo atributo na classe entity original e um novo atributo na classe "Entity" correspondente.</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">- Para critério de performance é recomendável utilizar chaves primárias numéricas (em ClientEntity utilizou-se o atributo login como @Id)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">2) (2.0) Armazene e Recupere as configurações pessoais dos Clientes com REDIS.</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">- ok</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">Extra:</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">* +0.5 devido a entrega do trabalho de JPA</p>', NULL);
