INSERT INTO public.notas VALUES (5250, 1, 891, 1245, '<p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">Para comparar igualdade de String, é recomendável utilizar o método equals (e não operador =)</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">&nbsp;</p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">Poderia não instanciar novamente, em cada ação dos botões (A e B) da tela que representa o Joystick. Os jogos ou até mesmo os Commands poderiam ser atributos instanciados uma única vez no construtor</p>', NULL);
