INSERT INTO public.notas VALUES (5257, 0.9, 891, 1304, '<p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;"><strong>Feedback anteriormente enviado.</strong></p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;"><em>"Não é o jogo (futebol e luta) que deve ter o apertouB1 e etc</em></p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;"><em>Pois não é o jogo que tem botões</em></p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;"><em>Quem tem botão em o joystick</em></p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;"><em>Uma ideia: O joystick recebe um slot (um comand)</em></p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;"><em>Cada command internamente, tem um jogo atrelado,</em></p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;"><em>E o objeto de joystick que dispara métodos de pressBotaoA&nbsp; e etc.</em><em>&nbsp;"</em></p><p style="color: rgb(0, 0, 0); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 10px;">&nbsp;</p>', NULL);
