INSERT INTO public.notas VALUES (5322, 3.5, 873, 1252, '<div>ALEXANDRA (3.5/4.0)</div><div>1) Aluguel de Filmes Digitais (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * Pagamento não é entidade válida para o problema (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * Empresa também poderia ser modelada como uma entidade isolada (evitando ciclos)</div><div>2) Livraria Digital&nbsp; (1.7/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp;* Exemplar deveria ser uma entidade fraca já que o atributo nro de sequência não é um bom atributo identificador (não é 100% único). O nro de sequência funcionaria melhor se fosse um atributo/chave parcial. (-0.2)</div><div>&nbsp; &nbsp; * faltou definir um atributo identificador para a entidade livros (-0.1)</div><div>* Observações:</div><div>&nbsp; &nbsp; * É recomendável que as entidades tenham nomes no singular e relacionamentos sejam definidos como verbos</div><div>&nbsp; &nbsp; * Livraria também pdoeria ser modelada como entidade isolada (evitando ciclos)</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5323, 2.7, 873, 1253, '<div>ALEXANDRE TOMAZ (2.7/4.0)</div><div>1) Aluguel de Filmes Digitais (1.2/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * código, número e vencimento do cartão não deveriam ser definidos como atributos derivados. (-0.2)</div><div>&nbsp; &nbsp; * categoria não deveria ser definida como uma entidade fraca. (-0.2)</div><div>&nbsp; &nbsp; * código deveria ser definido como um atributo identificador forte para entidade categoria (-0.2)</div><div>&nbsp; &nbsp; * atores não deveria ser uma entidade fraca pois id é um atributo identificador forte. Logo, deveria ser uma entidade forte. (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * é recomendável evitar loops em DER (ainda mais neste exercício que a modelagem exigia somente construção de um DER para uma única empresa de aluguel). Logo, todos os clientes são de uma mesma empresa. Logo, todos os filmes são desta mesma empresa.</div><div>&nbsp; &nbsp; * a entidade locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (1.5/2.0)</div><div>* Problemas:&nbsp;</div><div>&nbsp; &nbsp; * faltou definir herança entre as pessoas física e jurídica (-0.1)</div><div>&nbsp; &nbsp; * endereço não poderia ser uma entidade fraca. deveria ser um atributo composto da entidade cliente&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; * clientes comprar exemplares - e não - livros (-0.2)</div><div>&nbsp; &nbsp; * não é possível definir um atributo que armazene (cpf e cnpj) ao mesmo tempo&nbsp; &nbsp; &nbsp;(-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * recomendável nomear as entidades com nomes no singular</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5324, 3.4, 873, 1254, '<div>===========================================================================</div><div>ANA FLAVIA (3.4/4.0)</div><div>1) Aluguel de Filmes Digitais (1.6/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre locadora e cliente: como o exercício exigia a modelagem&nbsp; &nbsp; para somente uma locadora, os clientes são exclusivos desta locadora e devem ter cardinalidade (1,1) - e não - (1,n) (-0.2)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre filmes e locação: pois existir filmes que nunca foram alugados/locados. Logo a cardinalidade deveria ser (0,n) (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre livraria e cliente: como o exercício exigia a modelagem para somente uma livraria, os clientes são exclusivos desta locadora e devem ter cardinalidade (1,1) - e não - (1,n) (-0.2)</div><div>* Observações:</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5326, 3.4, 873, 1256, '<div>===========================================================================</div><div>DANIELLE SILVA (3.4/4.0)</div><div>1) Aluguel de Filmes Digitais (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * o atributo valor da entidade filme não poderia ser definido como um atributo derivado pois não pode ser obtido pelos outros atributos (-0.1)</div><div>&nbsp; &nbsp; * valor pago não poderia ser definido como sendo um atributo derivado da entidade locação (-0.1)</div><div>* Observações:</div><div>&nbsp; &nbsp; * a entidade locadora poderia ser caracterizada como uma entidade isolada</div><div>2) Livraria Digital (1.6/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * valor pago não poderia ser definido como atributo derivado pois não pode ser obtido por nenhum outro atributo (-0.1)</div><div>&nbsp; &nbsp; * valor da entidade livro também não poderia ser definido como atributo derivado pelo mesmo motivo (-0.1)</div><div>&nbsp; &nbsp; * faltou a entidade responsável por armazenar as informações da livraria (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * caracterizou corretamente exemplar como entidade fraca</div><div>&nbsp; &nbsp; * caracterizou corretamente nro de sequencia como atributo/chave parcial da entidade fraca exemplar</div><div>&nbsp; &nbsp; * definiu corretamente o tipo de herança</div><div>&nbsp; &nbsp; * compra foi definida como uma entidade forte mas poderia também ter sido definida como relacionamento e, consequentemente, este relacionamento teria que ter atributos descritivos atrelados</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5327, 2.7, 873, 1257, '<div>===========================================================================</div><div>DORIS HELENA (2.7/4.0)</div><div>1) Aluguel de Filmes Digitais (1.7/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * Não identificou atributo identificador na Entidade Ator (-0.1)</div><div>&nbsp; &nbsp; * Cardinalidade incorreta entre as entidades clientes e filme já que um filme pode ser alugado por vários clientes (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * Aluguel foi, corretamente, modelado como relacionamento - mas também - poderia também ser uma outra entidade.</div><div>&nbsp; &nbsp; * Empresa também poderia ser modelada como uma entidade isolada</div><div>2) Livraria Digital (1.0/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * Exemplar deveria ser uma entidade fraca já que o atributo nro de sequência não é um bom atributo identificador. O nro de sequência funcionaria melhor se fosse um atributo/chave parcial. (-0.2)</div><div>&nbsp; &nbsp; * Faltou definir/criar um atributo que armazenasse o valor pago pelo exemplar no momento da compra. (-0.1)</div><div>&nbsp; &nbsp; * Faltou definir o tipo (total ou parcial) de herança entre as entidades cliente, pessoa física e pessoa jurídica&nbsp; (-0.1)</div><div>&nbsp; &nbsp; * data não é um bom atributo identificador nem para entidades - tampouco para para relacionamentos (relacionamento compra) (-0.1)</div><div>&nbsp; &nbsp; * Cardinalidade mínima foi definida de forma incorreta entre cliente e exemplar (-0.2)</div><div>&nbsp; &nbsp; * Cardinalidade incorreta entre livraria e clientes&nbsp; (-0.2)</div><div>&nbsp; &nbsp; * telefone não deveria ser definido (neste exercício) como atributo multivalorado (-0.1)</div><div>* Observações:</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5328, 1.1, 873, 1258, '<div>EMANUEL ACOSTA (1.1/4.0)</div><div>1) Aluguel de Filmes Digitais (1.1/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * A entidade cliente não deveria estar ligada diretamente a entidade filmes pois o correto seria dizer que os clientes realizam Alugueis de filmes. (-0.2)</div><div>&nbsp; &nbsp; * Neste caso, categoria deveria uma entidade (-0.2)</div><div>&nbsp; &nbsp; * era preciso criar dois atributos (data inicio e data fim) na entidade aluguel (-0.2)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre as entidades locadora e cliente. O padrão correto é (mínima, máxima) (-0.1)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre as entidades cliente e filme. O padrão correto é (mínima, máxima) (-0.1)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre as entidades filme e aluguel. O padrão correto é (mínima, máxima) (-0.1)</div><div>* Observações:</div><div>&nbsp; &nbsp; * a entidade locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (0/2.0)</div><div>* exercício não realizado</div><div>===========================================================================</div><div>FERNANDO OLIVEIRA (3.0/4.0)</div>', NULL);
INSERT INTO public.notas VALUES (5329, 3, 873, 1259, '<div>===========================================================================</div><div>FERNANDO OLIVEIRA (3.0/4.0)</div><div>1) Aluguel de Filmes Digitais (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * ausência de atributo identificador na entidade aluguel. toda entidade forte precisa de um atributo identificador forte (-0.2)&nbsp; &nbsp; &nbsp; &nbsp;</div><div>* Observações:</div><div>&nbsp; &nbsp; * é recomendável evitar loops em DER (ainda mais neste exercício que a modelagem exigia somente construção de um DER para uma única empresa de aluguel). Logo, todos os clientes são de uma mesma empresa. Logo, todos os filmes são desta mesma empresa.</div><div>&nbsp; &nbsp; * a entidade locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (1.2/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * ausência da entidade fraca "exemplar" (-0.2)</div><div>&nbsp; &nbsp; * ausência da definição de cardinalidade entre as entidades livro e editora (-0.2)</div><div>&nbsp; &nbsp; * ausência da definição do tipo de herança (a herança era parcial ou total?) (-0.1)&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; * ausência de um relacionamento entre as entidades cliente e compra (-0.2)</div><div>&nbsp; &nbsp; * endereço é um atributo composto - e não - multivalorado (como foi definido) (-0.1)</div><div>* Observações:</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5330, 3.8, 873, 1260, '<div>FLAVIO (3.8)</div><div><br></div><div>1) CLINICA VETERINARIA (2/2)</div><div><br></div><div>- tudo ok</div><div><br></div><div>2) EMPRESA (1.8)</div><div><br></div><div>- departamento não deveria ser uma entidade fraca (-0.2)</div><div><br></div><div><br></div><div><br></div>', NULL);
INSERT INTO public.notas VALUES (5331, 1.8, 873, 1261, '<div>GABRIEL GARCIA CAMARGO SOARES (1.8/4.0)</div><div>1) Aluguel de Filmes Digitais (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre cliente e filmes pois um mesmo filme pode ser alugado por vários clientes; (-0.2)</div><div>* Observações:</div><div>2) Livraria Digital (0/2.0)</div><div>* não realizou o exercício</div>', NULL);
INSERT INTO public.notas VALUES (5332, 2.6, 873, 1262, '<div>GABRIEL MEDEIROS GARCIA (2.6/4.0)</div><div>1) Aluguel de Filmes Digitais (1.6/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * aluguel poderia ser uma entidade associativa ou relacionamento - e não - uma entidade fraca (-0.2)</div><div>&nbsp; &nbsp; * período não poderia ser definido como um atributo derivado (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (1/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * faltou definir o tipo da herança (é total ou parcial?) (-0.1)</div><div>&nbsp; &nbsp; * compra não poderia ser uma entidade fraca (-0.2)</div><div>&nbsp; &nbsp; * exemplar deveria ser uma entidade fraca (-0.2)</div><div>&nbsp; &nbsp; * nro de sequência deveria ser definido como um atributo/chave parcial&nbsp; (-0.2)</div><div>&nbsp; &nbsp; * editoras disponibilizam exemplares para livraria (-0.1)</div><div>&nbsp; &nbsp; * entidade compra incorreta entre as entidades editora e livraria (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * livraria poderia ser uma entidade isolada&nbsp; &nbsp;&nbsp;</div><div>=================================================</div>', NULL);
INSERT INTO public.notas VALUES (5333, 3.2, 873, 1263, '<div>===========================================================================</div><div>GABRYEL AIREZ (3.2/4.0)</div><div>1) Aluguel de Filmes Digitais (1.9/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * Uma entidade associativa deve ter atributos. Pois sem atributos, funciona basicamente como um relacionamento simples (binário ou ternário). O recomendável era que a entidade associativa aluga tivesse os atributos da entidade locação. (-0.1)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (1.3/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * símbolo de herança invertido (-0.1)</div><div>&nbsp; &nbsp; * atributo valor não pode ser um atributo derivado (-0.2)</div><div>&nbsp; &nbsp; * exemplar deveria ser uma entidade fraca (-0.2)</div><div>&nbsp; &nbsp; * livro não poderia ser uma entidade fraca (pois possuí um atributo identificador forte (isbn)) (-0.2)</div><div>* Observações:&nbsp;</div><div>&nbsp; &nbsp; * livraria poderia ser uma entidade isolada</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5334, 1.5, 873, 1264, '<div>INGRID (1.5/4.0)</div><div>1) Aluguel de Filmes Digitais (0.9/2.0)&nbsp; &nbsp;&nbsp;</div><div>* Problemas:</div><div>&nbsp; &nbsp; * a entidade cliente deveria estar relacionada com a entidade filme pois os clientes alugam filmes e não "histórico" (-0.2)</div><div>&nbsp; &nbsp; * a entidade histórico não deveria existir (-0.2)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * a entidade histórico não deveria ser uma entidade fraca (-0.1)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * a entidade histórico não deveria um atributo composto data aluguel com 2 sub-atributos (inicio e fim) (-0.1)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * o atributo nome do filme deveria ser atributo pertinente a entidade filme e não pertinente a entidade histórico. Existindo o atributo "título" na entidade filme e existindo "nome do filme" na entidade histórico temos uma duplicação de dados (algo fortemente não recomendado em bancos de dados) (-0.1)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre locadora e cliente: como o exercício exigia a modelagem para somente uma locadora, os clientes são exclusivos desta locadora e devem ter cardinalidade (1,1) - e não - (1,n) (-0.2)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre locadora e filme: como o exercício exigia a modelagem para somente uma locadora, os filmes são exclusivas desta locadora e devem ter cardinalidade (1,1) - e não - (1,n) (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (0.6/2.0)</div><div>* Problemas:</div><div>&nbsp;* cardinalidade incorreta entre livraria e cliente: como o exercício exigia a modelagem&nbsp; &nbsp; para somente uma livraria, os clientes são exclusivos desta locadora e devem ter cardinalidade (1,1) - e não - (1,n) (-0.2)</div><div>&nbsp; * cardinalidade incorreta entre livraria e livros: como o exercício exigia a modelagem para somente uma locadora, os livros são exclusivos desta locadora e devem ter cardinalidade (1,1) - e não - (1,n).&nbsp; (-0.2)</div><div>&nbsp; &nbsp; * A livraria possui exemplares - e não - livros (-0.2)</div><div>&nbsp; &nbsp; * ausência da entidade "editora" (-0.2)</div><div>&nbsp; &nbsp; * atributo sem nome (em branco) na entidade exemplar (-0.1)</div><div>&nbsp; &nbsp; * ausência de definição de herança para cadastrar pessoas físicas e jurídicas (-0.1)</div><div>&nbsp; &nbsp; * não é possível que um mesmo atributo tenha mais de uma nome (ex: CPF/CNPJ) (-0.2)</div><div>&nbsp; &nbsp; * exemplar deveria ser uma entidade fraca (-0.2)</div><div>&nbsp; &nbsp; * ausência de um relacionamento identificador entre a entidade exemplar (que deveria ser fraca) e livros (-0.1)</div><div>* Observações:&nbsp;</div><div>&nbsp; &nbsp; * recomendável definir os nomes das entidades no singular</div><div>&nbsp; &nbsp; * livraria poderia ser uma entidade isolada</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5336, 1.6, 873, 1266, '<div>===========================================================================</div><div>JOAO LUCAS (1.6/4.0)</div><div>1) Aluguel de Filmes Digitais (1.6/2.0)</div><div>* Problemas:&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; * Atributos ausentes da entidade filme (-0.2)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre as entidades filme e cliente (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser também definida como uma entidade isolada</div><div>2) Livraria Digital (0/2.0)</div><div>* Não realizou o exercício</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5337, 3.8, 873, 1267, '<div>===========================================================================</div><div>JOSUE (3.8/4.0)</div><div>1) Aluguel de Filmes Digitais (2/2.0)</div><div>* ok</div><div>2) Livraria Digital (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * representação incorreta para a chave/parcial nro de sequência na entidade fraca exemplar. Chaves parciais (de entidades fracas) deve estar sublinhadas de forma pontilhada. (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * poderia ter colocado a linha entre a entidade fraca exemplar e o relacionamento identificador "tem" como total (linhas duplas)</div><div>&nbsp; &nbsp; * compra poderia ser uma entidade associativa</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5339, 3, 873, 1269, '<div>===========================================================================</div><div>LUCAS AYANG (3.0/4.0)</div><div>1) Aluguel de Filmes Digitais (1.6/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * período do aluguel deveria ser representado por 2 atributos: data de início e data fim (-0.2)</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre as entidades cliente e filme: pois um mesmo filme pode ser alugado por vários clientes (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser uma entidade isolada&nbsp; &nbsp;&nbsp;</div><div>2) Livraria Digital (1.4/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * cpf não poderia ter sido definido como um atributo multivalorado (-0.2)</div><div>&nbsp; &nbsp; * não definiu clientes jurídicos (-0.2)</div><div>&nbsp; &nbsp; * ausência da entidade fraca "exemplar": (-0.2)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * clientes compram/adquirem exemplares - e não - livros</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * as editoras parceiras comercializam exemplares de livros - e não - livros diretamente.</div><div>* Observações:</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5340, 3.3, 873, 1270, '<div>===========================================================================</div><div>MANUELLA (3.3/4.0)</div><div>1) Aluguel de Filmes Digitais (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * o atributo valor não deveria ser multivalorado&nbsp; (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * a entidade empresa poderia ser uma entidade isolada</div><div>2) Livraria Digital (1.5/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * o atributo valor não deveria ser multivalorado (-0.2)</div><div>&nbsp; &nbsp; * entidade autor não tem o atributo "atributo" (-0.1)</div><div>&nbsp; &nbsp; * exemplar deveria ser uma entidade fraca (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * a entidade livraria poderia ser uma entidade isolada</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5343, 3.1, 873, 1273, '<div>===========================================================================</div><div>RAFAEL ALVES (3.1/4.0)</div><div>1) Aluguel de Filmes Digitais (1.6/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * cardinalidade incorreta entre as entidades locadora e clientes: como o exercício exigia a modelagem de um banco de dados para somente um única locadora os clientes são exclusivos desta locadora. Logo, a cardinalidade correta seria (1,1) tem (0,n) (-0.2)</div><div>&nbsp; &nbsp; * valor na entidade aluguel não poderia ser definido como um atributo multivalorado (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (1.5/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * não definiu o tipo da herança (é parcial ou total?) (-0.1)</div><div>&nbsp; &nbsp; * ausência da entidade fraca "exemplar" pois: (-0.2)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * clientes compram exemplares - e não - livros</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * as editoras parceiras disponibilizam/comercializam exemplares de livros.</div><div>&nbsp; &nbsp; &nbsp; &nbsp; * ausência do atributo nro de sequência (como chave/atributo parcial)</div><div>&nbsp; &nbsp; * atributo valor da entidade "compra" não poderia ser definido como um atributo multivalorado (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * livraria poderia ser uma entidade isolada</div><div>===========================================================================</div>', NULL);
INSERT INTO public.notas VALUES (5345, 3.1, 873, 1275, '<div>===========================================================================</div><div>RODRIGO ANTONIO (3.1/4.0)</div><div>1) Aluguel de Filmes Digitais (1.8/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * valor não poderia ser definido como um atributo derivado (-0.2)</div><div>* Observações:</div><div>&nbsp; &nbsp; * locadora poderia ser uma entidade isolada</div><div>2) Livraria Digital (1.3/2.0)</div><div>* Problemas:</div><div>&nbsp; &nbsp; * não definiu o tipo da herança (é parcial ou total?) (-0.1)</div><div>&nbsp; &nbsp; * valor não poderia ser definido como um atributo derivado (-0.2)</div><div>&nbsp; &nbsp; * não definiu nro de sequência como atributo/parcial da entidade fraca "exemplar" (-0.2)</div><div>&nbsp; &nbsp; * não definiu o relacionamento "refere-se" como relacionamento "identificador" (-0.1)</div><div>&nbsp; &nbsp; * autor/escritor deveriam ser a mesma entidade&nbsp; (-0.1)</div><div>* Observações:</div><div>===========================================================================</div>', NULL);
