INSERT INTO public.questoes VALUES (31, '<div>Construa um diagrama ER para um estabelecimento comercial onde se deseja:</div><div><br></div><div><ul><li>Registrar dados de produtos (código, nome, preço e quantidade em estoque).</li><li>Cada produto possui um fornecedor. É necessário registrar o nome, CNPJ e telefone do fornecedor.</li><li>Registrar vendas de produtos. Para cada venda existe uma nota fiscal associada. Cada nota deve possuir um ou mais itens, onde será registrada a quantidade comprada do produto.</li><li>Cada item está associado a um produto.</li><li>Cada venda é feita para um cliente, que deve ser cadastrado no sistema (registrando CPF, nome e telefone).</li></ul></div> ', '    ');
INSERT INTO public.questoes VALUES (13, '<div>Construa um sistema Web com as seguintes caracterı́sticas:</div><div><b><u><br></u></b></div><div><b><u>Observações:</u></b></div><div><ul><li>O objetivo do sistema é o cadastramento de pessoas e seus dependentes;</li><li>Um dependente é dependente de somente uma pessoa;</li><li>Cada Pessoa possui: id, nome, sobrenome e seus dependentes;</li><li>Cada Dependente possui: id, nome e sobrenome;</li></ul></div><div><br></div><div><b><u>Funcionalidades:</u></b></div><div><ul><li>Listar Pessoas</li><li>Listar Dependentes</li><li>Adicionar uma Pessoa</li><li>Adicionar um Dependente (relacionar o dependente a uma Pessoa)</li><li>Alterar uma Pessoa (os campos devem estar previamente preenchidos);</li><li>Alterar um Dependente (os campos devem estar previamente preenchidos);</li><li>Excluir uma Pessoa;</li><li>Quando excluir uma Pessoa é preciso excluir todos os Dependentes relaciona<span style="background-color: transparent; font-size: 14px;">dos;</span></li><li>Excluir um Dependente;</li></ul></div>', '   ');
INSERT INTO public.questoes VALUES (15, '<div>O objetivo é desenvolver o Google Keep Tabajara utilizando Python e PostgreSQL:</div><div><br></div><div>Cada nova anotação tem:</div><div><ul><li>Título *obrigatório</li><li>Descrição</li></ul></div><div><br></div><div><b><u>Funcionalidades Exigidas:</u></b></div><div><ul><li>Cadastrar novas anotações&nbsp;</li><li>Excluir anotações</li><li>Além de excluir definitivamente qualquer anotação, o Google Keep Tabajara apresenta também:</li><li>Enviar anotações para a Lixeira (repositório de anotações excluídas)</li><li>Restaurar anotações que estão na Lixeira&nbsp;</li><li>E excluir definitivamente qualquer anotação que está na Lixeira</li><li>Alterar anotações</li><li>Listar anotações</li><li>Copiar/Replicar uma anotação</li></ul></div>', '   ');
